const NAV_OPEN_CLASS_NAME='nav-open';const INPUT_VALID_CLASS_NAME='contact__input_valid';function toggleNavbar(){const n=document.getElementById('nav');if(n.classList.contains(NAV_OPEN_CLASS_NAME)){n.classList.remove(NAV_OPEN_CLASS_NAME);return}n.classList.add(NAV_OPEN_CLASS_NAME)}function toggleValid({target:t}){if(t.value){t.classList.add(INPUT_VALID_CLASS_NAME);return}t.classList.remove(INPUT_VALID_CLASS_NAME)}for(const i of document.getElementsByClassName('contact__input'))i.addEventListener('blur',toggleValid);
